// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@derify/derify-token/contracts/CommunityVestingVault.sol";

import "./interfaces/IDerifyBroker.sol";
import "./interfaces/IDerifyERC20.sol";

/// @title DerifyBroker - Derify broker moudle.
contract DerifyBroker is IDerifyBroker, Context, Ownable, AccessControl, ReentrancyGuard, Pausable, Initializable {
    /// @dev Add the library methods
    using EnumerableSet for EnumerableSet.AddressSet;
    
    /// @dev struct to store broker info
    struct BrokerInfo {
        // usd reawrd balance, could be nagative
        int256 usdRewardBalance;
        // accumulated usd reawrd balance, could be nagative
        int256 accumulatedUsdReward;
        // drf reawrd balance
        uint256 drfRewardBalance;
        // accumulated drf reawrd balance
        uint256 accumulatedDrfReward;
        // int without contract decimals
        uint256 validPeriodBlocks;
        // int without contract decimals, the block number of last time updated validPeriodBlocks
        uint256 blockNumber;    
        bool    isUsed;
    }
    
    /// @dev Create a new role identifier for the pauser role
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    /// @dev Create a new role identifier for the exchange role
    bytes32 public constant EXCHANGE_ROLE = keccak256("EXCHANGE_ROLE");
    /// @dev Create a new role identifier for the reward execute role
    bytes32 public constant REWARD_EXECUTE_ROLE = keccak256("REWARD_EXECUTE_ROLE");

    /// @dev The contract decimals
    uint256 public constant CONTRACT_DECIMAL = 10 ** 8;
    /// @dev The token contract decimals diff with contract decimals
    uint256 public constant TOKEN_DECIMAL_DIFF = 10 ** 10;

    /// @dev the token USDT contract address 
    address public tokenUSD;
    /// @dev the token DRF contract address 
    address public tokenDRF;
    /// @dev the token eDRF contract address 
    address public tokenEDRF;
    /// @dev the DerifyExchange contract address 
    address public exchange;
    /// @dev the CommunityVestingVault contract address 
    address public communityVestingVault;

    /// @inheritdoc IDerifyBroker
    uint256 public override brokerApplyNumber;
    /// @inheritdoc IDerifyBroker
    uint256 public override brokerValidUnitNumber;
    /// @inheritdoc IDerifyBroker
    uint256 public override usdRewardPool;
    /// @inheritdoc IDerifyBroker
    uint256 public override drfRewardPool;

    /// @dev all brokers
    EnumerableSet.AddressSet private allBrokers;
    /// @dev info of brokers
    mapping(address => BrokerInfo) private brokerInfos;

    constructor(
        address _exchange,
        address _tokenUSD,
        address _tokenDRF,
        address _tokenEDRF,
        address _communityVestingVault,
        uint256 _brokerApplyNumber,
        uint256 _brokerValidUnitNumber
    ) {
        tokenUSD = _tokenUSD;
        tokenDRF = _tokenDRF;
        tokenEDRF = _tokenEDRF;
        communityVestingVault = _communityVestingVault;
        brokerApplyNumber = _brokerApplyNumber;
        brokerValidUnitNumber = _brokerValidUnitNumber;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        _setupRole(REWARD_EXECUTE_ROLE, _msgSender());
        _setupRole(EXCHANGE_ROLE, _exchange);
    }

    /// @inheritdoc IDerifyBroker
    function updateParams(
        address _tokenUSD,
        address _tokenDRF,
        address _tokenEDRF,
        address _communityVestingVault,
        uint256 _brokerApplyNumber,
        uint256 _brokerValidUnitNumber
    ) external override onlyOwner {
        tokenUSD = _tokenUSD;
        tokenDRF = _tokenDRF;
        tokenEDRF = _tokenEDRF;
        communityVestingVault = _communityVestingVault;
        brokerApplyNumber = _brokerApplyNumber;
        brokerValidUnitNumber = _brokerValidUnitNumber;

        emit ContractParamsUpdate(
            tokenUSD, 
            tokenDRF,
            tokenEDRF,
            communityVestingVault,
            brokerApplyNumber,
            brokerValidUnitNumber
        );
    }

    /// @inheritdoc IDerifyBroker
    function initialBrokersForUpgrade(
        address[] calldata brokers, 
        uint256[] calldata validPeriodBlocks,
        uint256[] calldata lastUpdatedBlockNumbers
    ) external override onlyOwner initializer {
        uint256 len = brokers.length;
        require(len == validPeriodBlocks.length, "DBroker: LEN_DIFF_ERR");
        require(len == lastUpdatedBlockNumbers.length, "DBroker: LEN_DIFF_ERR");
        for(uint256 i = 0; i < len; i++) {
            if (!allBrokers.contains(brokers[i]) && !brokerInfos[brokers[i]].isUsed) {
                allBrokers.add(brokers[i]);
                brokerInfos[brokers[i]].isUsed = true;
                brokerInfos[brokers[i]].validPeriodBlocks = validPeriodBlocks[i];
                brokerInfos[brokers[i]].blockNumber = lastUpdatedBlockNumbers[i];
            }
        }
    }

    /// @inheritdoc IDerifyBroker
    function removeBrokers(address[] calldata brokers) external override onlyOwner {
        for(uint256 i = 0; i < brokers.length; i++) {
            if (allBrokers.contains(brokers[i])) {
                allBrokers.remove(brokers[i]);
                delete brokerInfos[brokers[i]];
            }
        }
    }

    /// @inheritdoc IDerifyBroker
    function pause() external override onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @inheritdoc IDerifyBroker
    function unpause() external override onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /// @inheritdoc IDerifyBroker
    function addBrokerReward(
        address broker,
        address trader,
        uint256 amount
    ) external override onlyRole(EXCHANGE_ROLE) {
        brokerInfos[broker].usdRewardBalance += int256(amount);
        brokerInfos[broker].accumulatedUsdReward += int256(amount);
        usdRewardPool += amount;
        emit UsdRewardBalanceUpdate(
            UpdateType.Income, 
            broker, 
            trader, 
            int256(amount), 
            brokerInfos[broker].usdRewardBalance, 
            brokerInfos[broker].accumulatedUsdReward,
            block.timestamp
        );
        emit UsdRewardPoolUpdate(UpdateType.Income, int256(amount), usdRewardPool, block.timestamp);
    }

    // claim DRF from CommunityVestingVault
    function claimDrfReward() external override onlyRole(REWARD_EXECUTE_ROLE) {
        (, uint256 amountVested) = CommunityVestingVault(communityVestingVault).calculateGrantClaim(address(this));
        CommunityVestingVault(communityVestingVault).claimVestedTokens();
        drfRewardPool += amountVested / TOKEN_DECIMAL_DIFF;
        emit DrfRewardPoolUpdate(UpdateType.Income, int256(amountVested / TOKEN_DECIMAL_DIFF), drfRewardPool, block.timestamp);
    }

    /// @inheritdoc IDerifyBroker
    function updateDrfReward(
        address[] calldata brokers,
        uint256[] calldata drfAmounts,
        uint256[] calldata gasFeeAmounts
    ) external override onlyRole(REWARD_EXECUTE_ROLE) {
        uint256 len = brokers.length;
        require(len == drfAmounts.length, "DBroker: UDR_DRF_LEN_ERR");
        require(len == gasFeeAmounts.length, "DBroker: UDR_GASFEE_LEN_ERR");
        uint256 totalGasFee;
        for(uint256 i = 0; i < len; i++) {
            address broker = brokers[i];
            // update DRF reward
            brokerInfos[broker].drfRewardBalance += drfAmounts[i];
            brokerInfos[broker].accumulatedDrfReward += drfAmounts[i];
            emit DrfRewardBalanceUpdate(
                UpdateType.Income, 
                broker, 
                int256(drfAmounts[i]), 
                brokerInfos[broker].drfRewardBalance, 
                brokerInfos[broker].accumulatedDrfReward,
                block.timestamp
            );
            // sub gas fee from usd reward
            brokerInfos[broker].usdRewardBalance -= int256(gasFeeAmounts[i]);
            totalGasFee += gasFeeAmounts[i];
            emit UsdRewardBalanceUpdate(
                UpdateType.GasFee, 
                broker, 
                broker, 
                int256(gasFeeAmounts[i]) * -1, 
                brokerInfos[broker].usdRewardBalance,
                brokerInfos[broker].accumulatedUsdReward,
                block.timestamp
            );
        }
        // handle the case usdRewardPool < totalGasFee in backend
        require(usdRewardPool >= totalGasFee, "DBroker: UDR_USD_BALANCE_EXCEED");
        usdRewardPool -= totalGasFee;
        require(IERC20(tokenUSD).transfer(_msgSender(), totalGasFee * TOKEN_DECIMAL_DIFF), "DBroker: UDR_USD_TRANSFER_ERR");
        emit UsdRewardPoolUpdate(UpdateType.GasFee, int256(totalGasFee) * -1, usdRewardPool, block.timestamp);
    }

    /// @inheritdoc IDerifyBroker
    function withdrawBrokerReward() external override nonReentrant whenNotPaused {
        address broker = _msgSender();
        // withdraw USD reward
        if (brokerInfos[broker].usdRewardBalance > 0) {
            uint256 usdAmount = uint256(brokerInfos[broker].usdRewardBalance);
            require(usdRewardPool >= usdAmount, "DBroker: WBR_USD_BALANCE_EXCEED");
            brokerInfos[broker].usdRewardBalance -= int256(usdAmount);
            usdRewardPool -= usdAmount;
            require(IERC20(tokenUSD).transfer(broker, usdAmount * TOKEN_DECIMAL_DIFF), "DBroker: WBR_USD_TRANSFER_ERR");
            emit UsdRewardBalanceUpdate(
                UpdateType.Withdraw, 
                broker, 
                broker, 
                int256(usdAmount) * -1, 
                brokerInfos[broker].usdRewardBalance,
                brokerInfos[broker].accumulatedUsdReward, 
                block.timestamp
            );
            emit UsdRewardPoolUpdate(UpdateType.Withdraw, int256(usdAmount) * -1, usdRewardPool, block.timestamp);
        }
        // withdraw DRF reward
        if (brokerInfos[broker].drfRewardBalance > 0) {
            uint256 drfAmount = brokerInfos[broker].drfRewardBalance;
            require(drfRewardPool >= drfAmount, "DBroker: WBR_DRF_BALANCE_EXCEED");
            brokerInfos[broker].drfRewardBalance -= drfAmount;
            drfRewardPool -= drfAmount;
            require(IERC20(tokenDRF).transfer(broker, drfAmount * TOKEN_DECIMAL_DIFF), "DBroker: WBR_DRF_TRANSFER_ERR");
            emit DrfRewardBalanceUpdate(
                UpdateType.Withdraw, 
                broker, 
                int256(drfAmount) * -1, 
                brokerInfos[broker].drfRewardBalance, 
                brokerInfos[broker].accumulatedDrfReward, 
                block.timestamp
            );
            emit DrfRewardPoolUpdate(UpdateType.Withdraw, int256(drfAmount) * -1, drfRewardPool, block.timestamp);
        }
    }

    /// @inheritdoc IDerifyBroker
    function applyBroker() external override {
        address broker = _msgSender();
        // only burn edrf for not used user
        if (!allBrokers.contains(broker) && !brokerInfos[broker].isUsed) {
            IDerifyERC20(tokenEDRF).burn(broker, brokerApplyNumber * TOKEN_DECIMAL_DIFF);
            allBrokers.add(broker);
            brokerInfos[broker].isUsed = true;

            emit BrokerQualificationUpdate(
                QualificationType.Apply, 
                broker, 
                brokerApplyNumber, 
                brokerApplyNumber, 
                block.timestamp
            );
        }
    }

    /// @inheritdoc IDerifyBroker
    function burnEdrfExtendValidPeriod(uint256 amount) external override {
        address trader = _msgSender();
        require(brokerInfos[trader].isUsed, "DBroker: BEEVP_NOT_BROKER");
        // burn edrf
        IDerifyERC20(tokenEDRF).burn(trader, amount * TOKEN_DECIMAL_DIFF);
        emit BrokerQualificationUpdate(
            QualificationType.Extend, 
            trader, 
            amount, 
            brokerValidUnitNumber, 
            block.timestamp
        );
        // update valid period
        uint256 newAddedPeriodBlocks = amount / brokerValidUnitNumber;
        if (block.number - brokerInfos[trader].blockNumber >= brokerInfos[trader].validPeriodBlocks) {
            brokerInfos[trader].validPeriodBlocks = newAddedPeriodBlocks;
        } else {
            brokerInfos[trader].validPeriodBlocks = brokerInfos[trader].validPeriodBlocks + newAddedPeriodBlocks - (block.number - brokerInfos[trader].blockNumber);
        }
        brokerInfos[trader].blockNumber = block.number;
    }

    /// @inheritdoc IDerifyBroker
    function getBrokerInfo(address broker) external view override returns (
        int256 usdRewardBalance, 
        int256 accumulatedUsdReward, 
        uint256 drfRewardBalance, 
        uint256 accumulatedDrfReward, 
        uint256 validPeriodInBlocks, 
        uint256 lastUpdatedBlockNumber
    ) {
        require (brokerInfos[broker].isUsed, "DBroker: GBI_NOT_BROKER");
        
        usdRewardBalance = brokerInfos[broker].usdRewardBalance;
        accumulatedUsdReward = brokerInfos[broker].accumulatedUsdReward;
        drfRewardBalance = brokerInfos[broker].drfRewardBalance;
        accumulatedDrfReward = brokerInfos[broker].accumulatedDrfReward;
        // int without contract decimals
        validPeriodInBlocks = brokerInfos[broker].validPeriodBlocks;
        lastUpdatedBlockNumber = brokerInfos[broker].blockNumber;
    }

    /// @inheritdoc IDerifyBroker
    function getAllBrokers() external view override returns(address[] memory) {
        return allBrokers.values();
    }
}
