// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.4;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

import "./interfaces/IDerifyExchange.sol";
import "./interfaces/IDerifyDerivative.sol";
import "./interfaces/IDerifyFactory.sol";

import "./libraries/DerifyMath.sol";
import "./libraries/DerifyHelper.sol";

contract DerifyDerivative is IDerifyDerivative, Context, AccessControlEnumerable {
    /// @dev  Add the library methods
    using EnumerableSet for EnumerableSet.AddressSet;

    /// @dev  Create a new role identifier for the caller role
    bytes32 public constant CALLER_ROLE = keccak256("CALLER_ROLE");

    /// @dev the exchange address 
    address public exchange;
    /// @dev the token of the derivative 
    address public token;

    /// @inheritdoc IDerifyDerivative
    uint256 public override tradingFeeRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override tradingFeePmrRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override tradingFeeInusranceRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override tradingFeeBrokerRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override kRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override gRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override roRatio;
    /// @inheritdoc IDerifyDerivative
    uint256 public override maxLeverage;
    /// @inheritdoc IDerifyDerivative
    uint256 public override maxLimitOrderSize;

    /// @inheritdoc IDerifyDerivative
    uint256 public override longTotalSize;
    /// @inheritdoc IDerifyDerivative
    uint256 public override shortTotalSize;
    /// @inheritdoc IDerifyDerivative
    uint256 public override longAveragePrice;
    /// @inheritdoc IDerifyDerivative
    uint256 public override shortAveragePrice;

    /// @notice The contract decimals
    uint256 public constant CONTRACT_DECIMAL = 10**8;

    /// @dev chainlink price feed
    AggregatorV3Interface internal priceFeed;

    /// @dev the trader positions under the derivative 
    mapping(address => DerivativePositions) private traderAllPositions;
    /// @dev the set of all traders
    EnumerableSet.AddressSet private allTraders;

    constructor(address _token) {
        token = _token;
        _setupRole(DEFAULT_ADMIN_ROLE, IDerifyFactory(_msgSender()).owner());
    }

    /// @inheritdoc IDerifyDerivative
    function initialize(
        address _exchange,
        address _clearing,
        address _aggregator,
        uint256 _tradingFeeRatio,
        uint256 _tradingFeePmrRatio,
        uint256 _tradingFeeInusranceRatio,
        uint256 _tradingFeeBrokerRatio,
        uint256 _kRatio,
        uint256 _gRatio,
        uint256 _roRatio,
        uint256 _maxLeverage,
        uint256 _maxLimitOrderSize
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        // setup roles
        _setupRole(CALLER_ROLE, _exchange);
        _setupRole(CALLER_ROLE, _clearing);

        exchange = _exchange;
        priceFeed = AggregatorV3Interface(_aggregator);
        
        tradingFeeRatio = _tradingFeeRatio;
        tradingFeePmrRatio = _tradingFeePmrRatio;
        tradingFeeInusranceRatio = _tradingFeeInusranceRatio;
        tradingFeeBrokerRatio = _tradingFeeBrokerRatio;
        kRatio = _kRatio;
        gRatio = _gRatio;
        roRatio = _roRatio;
        maxLeverage = _maxLeverage;
        maxLimitOrderSize = _maxLimitOrderSize;
    }

    /// @inheritdoc IDerifyDerivative
    function openMarketPricePosition(
        TradeType tradeType,
        address trader,
        address theBroker,
        Side side, 
        uint256 size, 
        uint256 openPrice,
        uint256 leverage
    ) external override onlyRole(CALLER_ROLE) {
        require(leverage <= maxLeverage, "DD_OMPP: MAX_LEVERAGE_EXCEED");
        if (!allTraders.contains(trader) && !traderAllPositions[trader].isUsed) {
            allTraders.add(trader);
            traderAllPositions[trader].isUsed = true;
        }
        // compute position change fee ratio before position open
        int256 pcfRatioBefore = DerifyMath.abs(_getPositionChangeFeeRatio());
        int256 nakedPositionBefore = DerifyMath.abs(int256(longTotalSize) - int256(shortTotalSize));
        // open position and update status
        if (side == Side.LONG) {
            if (!traderAllPositions[trader].long.isUsed) {
                traderAllPositions[trader].long = Position(true, size, openPrice, leverage, block.timestamp);
            } else {
                (
                    traderAllPositions[trader].long.price,
                    traderAllPositions[trader].long.size
                ) = DerifyHelper.computeMergePosition(
                    traderAllPositions[trader].long.price,
                    traderAllPositions[trader].long.size,
                    openPrice,
                    size
                );
                traderAllPositions[trader].long.leverage = leverage;
            }
            // update global price and size
            (
                longAveragePrice,
                longTotalSize
            ) = DerifyHelper.computeMergePosition(
                longAveragePrice,
                longTotalSize,
                openPrice,
                size
            );
            emit GlobleDataUpdate(Side.LONG, longTotalSize, longAveragePrice, block.timestamp);
        }
        else {
            if (!traderAllPositions[trader].short.isUsed) {
                traderAllPositions[trader].short = Position(true, size, openPrice, leverage, block.timestamp);
            } else {
                (
                    traderAllPositions[trader].short.price,
                    traderAllPositions[trader].short.size
                ) = DerifyHelper.computeMergePosition(
                    traderAllPositions[trader].short.price,
                    traderAllPositions[trader].short.size,
                    openPrice,
                    size
                );
                traderAllPositions[trader].short.leverage = leverage;
            }
            // update global price and size
            (
                shortAveragePrice,
                shortTotalSize
            ) = DerifyHelper.computeMergePosition(
                shortAveragePrice,
                shortTotalSize,
                openPrice,
                size
            );
            emit GlobleDataUpdate(Side.SHORT, shortTotalSize, shortAveragePrice, block.timestamp);
        }
        // compute and operate trading fee
        uint256 tradingFee = size * openPrice * tradingFeeRatio / CONTRACT_DECIMAL / CONTRACT_DECIMAL;
        _operateTradingFee(tradingFee, trader, theBroker);
        // compute and operate position change fee
        int256 positionChangeFee;
        if (tradeType != TradeType.HedgeMarketPriceOpen) {
            int256 pcfRatioAfter = DerifyMath.abs(_getPositionChangeFeeRatio());
            int256 nakedPositionAfter = DerifyMath.abs(int256(longTotalSize) - int256(shortTotalSize));
            positionChangeFee = _getPositionChangeFee(
                nakedPositionAfter - nakedPositionBefore, 
                pcfRatioBefore + pcfRatioAfter
            );
            _operatePositionChangeFee(positionChangeFee, trader);
        }
        // emit event
        emit Open(
            trader, 
            token, 
            tradeType, 
            side, 
            size, 
            openPrice, 
            leverage, 
            tradingFee,
            positionChangeFee,
            block.timestamp
        );
    }

    /// @inheritdoc IDerifyDerivative
    function orderLimitPricePosition(
        address trader,
        Side side, 
        uint256 size, 
        uint256 limitPrice, 
        uint256 leverage
    ) external override onlyRole(CALLER_ROLE) {
        require(leverage <= maxLeverage, "DD_OLPP: MAX_LEVERAGE_EXCEED");
        if (!allTraders.contains(trader) && !traderAllPositions[trader].isUsed) {
            allTraders.add(trader);
            traderAllPositions[trader].isUsed = true;
        }

        uint256 currentSize = traderAllPositions[trader].longOrderOpenPosition.length + traderAllPositions[trader].shortOrderOpenPosition.length;
        require(currentSize < maxLimitOrderSize, "DD_OLPP: MAX_LOS_EXCEED");
        Position memory position = Position(true, size, limitPrice, leverage, block.timestamp);
        if (side == Side.LONG) {
            traderAllPositions[trader].longOrderOpenPosition.push(position);
        }
        else {
            traderAllPositions[trader].shortOrderOpenPosition.push(position);
        }
        emit OpenLimit(trader, token, side, size, limitPrice, leverage, block.timestamp);
    }

    /// @inheritdoc IDerifyDerivative
    function closePositionByTrader(
        TradeType tradeType,
        address trader,
        address theBroker,
        Side side, 
        uint256 size
    ) external override onlyRole(CALLER_ROLE) {
        _closePositionByTrader(tradeType, trader, theBroker, side, size);
    }

    /// @inheritdoc IDerifyDerivative
    function closePositionByLiquidation(
        address trader,
        address theBroker,
        Side side
    ) external override onlyRole(CALLER_ROLE) {
        uint256 size;
        if (side == Side.LONG) {
            if (!traderAllPositions[trader].long.isUsed) {
                return;
            }
            size = traderAllPositions[trader].long.size;
        } else {
            if (!traderAllPositions[trader].short.isUsed) {
                return;
            }
            size = traderAllPositions[trader].short.size;
        }
        uint256 spotPrice = _getSpotPrice();

        _closePositionCommonPart(TradeType.MandatoryLiquidationClose, trader, theBroker, side, size, spotPrice);
    }

    /// @inheritdoc IDerifyDerivative
    function closeDerivativePositions(
        address trader,
        address theBroker
    ) external override onlyRole(CALLER_ROLE) {
        // close same size for both long and short position
        if (traderAllPositions[trader].long.size > 0 && traderAllPositions[trader].short.size > 0) {
            if (traderAllPositions[trader].long.size >= traderAllPositions[trader].short.size) {
                _closePositionByTrader(
                    TradeType.AllCloseHedgePart, 
                    trader, 
                    theBroker, 
                    Side.LONG, 
                    traderAllPositions[trader].short.size
                );
                _closePositionByTrader(
                    TradeType.AllCloseHedgePart, 
                    trader, 
                    theBroker, 
                    Side.SHORT, 
                    traderAllPositions[trader].short.size
                );  
            } else {
                _closePositionByTrader(
                    TradeType.AllCloseHedgePart, 
                    trader, 
                    theBroker, 
                    Side.SHORT, 
                    traderAllPositions[trader].long.size
                );
                _closePositionByTrader(
                    TradeType.AllCloseHedgePart, 
                    trader, 
                    theBroker, 
                    Side.LONG, 
                    traderAllPositions[trader].long.size
                );
            }
        }
        // close left long position
        if (traderAllPositions[trader].long.size > 0) {
            uint256 longMaxSize = IDerifyExchange(exchange).getCloseUpperBound(token, trader, Side.LONG);
            if (longMaxSize > 0) {
                if (traderAllPositions[trader].long.size > longMaxSize) {
                    _closePositionByTrader(TradeType.AllCloseLeftPart, trader, theBroker, Side.LONG, longMaxSize);
                } else {
                    _closePositionByTrader(
                        TradeType.AllCloseLeftPart, 
                        trader, 
                        theBroker, 
                        Side.LONG, 
                        traderAllPositions[trader].long.size
                    );
                }
            }
        }
        // close left short position
        if (traderAllPositions[trader].short.size > 0) {
            uint256 shortMaxSize = IDerifyExchange(exchange).getCloseUpperBound(token, trader, Side.SHORT);
            if (shortMaxSize > 0) {
                if (traderAllPositions[trader].short.size > shortMaxSize) {
                    _closePositionByTrader(TradeType.AllCloseLeftPart, trader, theBroker, Side.SHORT, shortMaxSize);
                } else {
                    _closePositionByTrader(
                        TradeType.AllCloseLeftPart, 
                        trader, 
                        theBroker, 
                        Side.SHORT, 
                        traderAllPositions[trader].short.size
                    );
                }
            }
        }
    }

    /// @inheritdoc IDerifyDerivative
    function cancelDerivativeOrderedPositions(
        address trader
    ) external override onlyRole(CALLER_ROLE) {
        // cancel ordered stop positions
        delete traderAllPositions[trader].longOrderStopProfitPosition;
        delete traderAllPositions[trader].longOrderStopLossPosition;
        delete traderAllPositions[trader].shortOrderStopProfitPosition;
        delete traderAllPositions[trader].shortOrderStopLossPosition;
        // cancel ordered limit price positions
        delete traderAllPositions[trader].longOrderOpenPosition;
        delete traderAllPositions[trader].shortOrderOpenPosition;
        // emit event
        emit CancelOrderedPositions(trader, token, block.timestamp);
    }

    /// @inheritdoc IDerifyDerivative
    function deleteOrderedLimitPositions(
        address trader,
        Side side,
        uint256 index,
        uint256 size,
        uint256 price,
        uint256 leverage,
        uint256 limitTimestamp
    ) external override onlyRole(CALLER_ROLE) {
        Position[] storage poops = traderAllPositions[trader].longOrderOpenPosition;
        if(side == Side.SHORT) {
            poops = traderAllPositions[trader].shortOrderOpenPosition;
        }
        uint256 len = poops.length;
        for(uint256 k = 0; k < len; k++) {
            // find the right position
            if (poops[k].price == price && poops[k].timestamp == limitTimestamp && poops[k].leverage == leverage) {
                if (poops[k].size == size) {
                    // move the last element into the place to delete element
                    poops[k] = poops[len - 1];
                    poops.pop();
                } else {
                    // update element
                    poops[k].size -= size;
                }

                break;
            }
        }

        emit CloseLimit(trader, token, side, size, price, leverage, limitTimestamp, block.timestamp);
    }

    /// @inheritdoc IDerifyDerivative
    function removeTrader(address trader) external override onlyRole(CALLER_ROLE) {
        allTraders.remove(trader);
        delete traderAllPositions[trader];
    }

    /// @inheritdoc IDerifyDerivative
    function orderStopPosition(
        Side side,
        StopType stopType,
        uint256 takeProfitPrice,
        uint256 stopLossPrice
    ) external override {
        address trader = _msgSender();
        if (stopType == StopType.TakeProfit || stopType == StopType.TakeProfitStopLoss) {
            if (side == Side.LONG && traderAllPositions[trader].long.isUsed) {
                traderAllPositions[trader].longOrderStopProfitPosition = StopPosition(true, takeProfitPrice, block.timestamp);
            } else if (side == Side.SHORT && traderAllPositions[trader].short.isUsed) {
                traderAllPositions[trader].shortOrderStopProfitPosition = StopPosition(true, takeProfitPrice, block.timestamp);
            }
            emit OpenStop(trader, token, side, StopType.TakeProfit, takeProfitPrice, block.timestamp);
        }
        if (stopType == StopType.StopLoss || stopType == StopType.TakeProfitStopLoss) {
            if (side == Side.LONG && traderAllPositions[trader].long.isUsed) {
                traderAllPositions[trader].longOrderStopLossPosition = StopPosition(true, stopLossPrice, block.timestamp);
            } else if (side == Side.SHORT && traderAllPositions[trader].short.isUsed) {
                traderAllPositions[trader].shortOrderStopLossPosition = StopPosition(true, stopLossPrice, block.timestamp);
            }
            emit OpenStop(trader, token, side, StopType.StopLoss, stopLossPrice, block.timestamp);
        }
    }

    /// @inheritdoc IDerifyDerivative
    function orderAndCancelStopPosition(
        Side side,
        StopType orderStopType,
        uint256 orderPrice,
        StopType cancelStopType
    ) external override {
        address trader = _msgSender();
        if (side == Side.LONG) {
            if (orderStopType == StopType.TakeProfit && cancelStopType == StopType.StopLoss) {
                traderAllPositions[trader].longOrderStopProfitPosition = StopPosition(true, orderPrice, block.timestamp);
                delete traderAllPositions[trader].longOrderStopLossPosition;
            } else if (orderStopType == StopType.StopLoss && cancelStopType == StopType.TakeProfit) {
                traderAllPositions[trader].longOrderStopLossPosition = StopPosition(true, orderPrice, block.timestamp);
                delete traderAllPositions[trader].longOrderStopProfitPosition;
            }
        } else {
            if (orderStopType == StopType.TakeProfit && cancelStopType == StopType.StopLoss) {
                traderAllPositions[trader].shortOrderStopProfitPosition = StopPosition(true, orderPrice, block.timestamp);
                delete traderAllPositions[trader].shortOrderStopLossPosition;
            } else if (orderStopType == StopType.StopLoss && cancelStopType == StopType.TakeProfit) {
                traderAllPositions[trader].shortOrderStopLossPosition = StopPosition(true, orderPrice, block.timestamp);
                delete traderAllPositions[trader].shortOrderStopProfitPosition;
            }
        }
        emit OpenStop(trader, token, side, orderStopType, orderPrice, block.timestamp);
        emit CloseStop(trader, token, side, cancelStopType, block.timestamp);
    }

    /// @inheritdoc IDerifyDerivative
    function cancelOrderedStopPosition(
        StopType stopType,
        Side side
    ) external override {
        address trader = _msgSender();
        if (side == Side.LONG) {
            if (stopType == StopType.TakeProfit || stopType == StopType.TakeProfitStopLoss) {
                delete traderAllPositions[trader].longOrderStopProfitPosition;
            } 
            if (stopType == StopType.StopLoss || stopType == StopType.TakeProfitStopLoss) {
                delete traderAllPositions[trader].longOrderStopLossPosition;
            }
        } else if (side == Side.SHORT){
            if (stopType == StopType.TakeProfit || stopType == StopType.TakeProfitStopLoss) {
                delete traderAllPositions[trader].shortOrderStopProfitPosition;
            } 
            if (stopType == StopType.StopLoss || stopType == StopType.TakeProfitStopLoss) {
                delete traderAllPositions[trader].shortOrderStopLossPosition;
            }
        }
        emit CloseStop(trader, token, side, stopType, block.timestamp);
    }

    /// @inheritdoc IDerifyDerivative
    function cancelOrderedLimitPosition(
        Side side,
        uint256 timestamp
    ) external override {
        address trader = _msgSender();
        if (side == Side.LONG) {
            for(uint i = 0; i < traderAllPositions[trader].longOrderOpenPosition.length; i++) {
                if (traderAllPositions[trader].longOrderOpenPosition[i].timestamp == timestamp) {
                    emit CloseLimit(
                        trader, 
                        token, 
                        Side.LONG, 
                        traderAllPositions[trader].longOrderOpenPosition[i].size, 
                        traderAllPositions[trader].longOrderOpenPosition[i].price, 
                        traderAllPositions[trader].longOrderOpenPosition[i].leverage,
                        traderAllPositions[trader].longOrderOpenPosition[i].timestamp, 
                        block.timestamp
                    );
                    traderAllPositions[trader].longOrderOpenPosition[i] = traderAllPositions[trader].longOrderOpenPosition[traderAllPositions[trader].longOrderOpenPosition.length - 1];
                    traderAllPositions[trader].longOrderOpenPosition.pop();
                    break;
                }
            }
        } else {
            for(uint i = 0; i < traderAllPositions[trader].shortOrderOpenPosition.length; i++) {
                if (traderAllPositions[trader].shortOrderOpenPosition[i].timestamp == timestamp) {
                    emit CloseLimit(
                        trader, 
                        token, 
                        Side.SHORT, 
                        traderAllPositions[trader].shortOrderOpenPosition[i].size, 
                        traderAllPositions[trader].shortOrderOpenPosition[i].price, 
                        traderAllPositions[trader].shortOrderOpenPosition[i].leverage,
                        traderAllPositions[trader].shortOrderOpenPosition[i].timestamp, 
                        block.timestamp
                    );
                    traderAllPositions[trader].shortOrderOpenPosition[i] = traderAllPositions[trader].shortOrderOpenPosition[traderAllPositions[trader].shortOrderOpenPosition.length - 1];
                    traderAllPositions[trader].shortOrderOpenPosition.pop();
                    break;
                }
            }
        }
    }

