    /// @inheritdoc IDerifyDerivative
    function getTokenTotalUnrealizedPnl() external view override returns (int256 tokenTotalUnrealizedPnl) {
        uint256 spotPrice = _getSpotPrice();
        tokenTotalUnrealizedPnl = int256(longTotalSize) * (int256(spotPrice) - int256(longAveragePrice)) / int256(CONTRACT_DECIMAL);
        tokenTotalUnrealizedPnl += int256(shortTotalSize) * (int256(shortAveragePrice) - int256(spotPrice)) / int256(CONTRACT_DECIMAL);
    }

