    }

    /// @dev close position 
    function _closePositionByTrader(
        TradeType tradeType,
        address trader,
        address theBroker,
        Side side, 
        uint256 size
    ) internal {
        require(size > 0, "DD: CPBT_SIZE_ZERO");
        uint256 closePrice = _getSpotPrice();
        // use price of ordered position as close price
        if (tradeType == TradeType.StopProfitClose) {
            if (side == Side.LONG) {
                closePrice = traderAllPositions[trader].longOrderStopProfitPosition.stopPrice;
            } else {
                closePrice = traderAllPositions[trader].shortOrderStopProfitPosition.stopPrice;
            }
        } else if (tradeType == TradeType.StopLossClose) {
            if (side == Side.LONG) {
                closePrice = traderAllPositions[trader].longOrderStopLossPosition.stopPrice;
            } else {
                closePrice = traderAllPositions[trader].shortOrderStopLossPosition.stopPrice;
            }
        }

        _closePositionCommonPart(tradeType, trader, theBroker, side, size, closePrice);
    }

    /// @dev the common part of close position 
    function _closePositionCommonPart(
        TradeType tradeType,
        address trader,
        address theBroker,
        Side side, 
        uint256 size,
        uint256 closePrice
    ) internal {
        // compute and operate unrealized profit and loss
        int256 unrealizedPnl = _getUnrealizedPnlFixedSize(side, trader, size, closePrice);
        _operateProfitAndLoss(unrealizedPnl, trader);
        // compute position change fee before close the position
        int256 pcfRatioBefore = DerifyMath.abs(_getPositionChangeFeeRatio());
        int256 nakedPositionBefore = DerifyMath.abs(int256(longTotalSize) - int256(shortTotalSize));
        // close position
        if (side == Side.LONG) {
            // update global price and size
            (
                longAveragePrice,
                longTotalSize
            ) = DerifyHelper.computeDetachPosition(
                longAveragePrice,
                longTotalSize,
                traderAllPositions[trader].long.price,
                size
            );
            emit GlobleDataUpdate(Side.LONG, longTotalSize, longAveragePrice, block.timestamp);
            if (traderAllPositions[trader].long.size == size) {
                delete traderAllPositions[trader].long;
                // close related stop positions
                if (traderAllPositions[trader].longOrderStopProfitPosition.isUsed) {
                    delete traderAllPositions[trader].longOrderStopProfitPosition;
                    emit CloseStop(trader, token, side, StopType.TakeProfit, block.timestamp);
                }
                if (traderAllPositions[trader].longOrderStopLossPosition.isUsed) {
                    delete traderAllPositions[trader].longOrderStopLossPosition;
                    emit CloseStop(trader, token, side, StopType.StopLoss, block.timestamp);
                }
            } else {
                traderAllPositions[trader].long.size = traderAllPositions[trader].long.size - size;
                traderAllPositions[trader].long.timestamp = block.timestamp;
            }
        } else {
            // update global price and size
            (
                shortAveragePrice,
                shortTotalSize
            ) = DerifyHelper.computeDetachPosition(
                shortAveragePrice,
                shortTotalSize,
                traderAllPositions[trader].short.price,
                size
            );
            emit GlobleDataUpdate(Side.SHORT, shortTotalSize, shortAveragePrice, block.timestamp);
            if (traderAllPositions[trader].short.size == size) {
                delete traderAllPositions[trader].short;
                // close related stop positions
                if (traderAllPositions[trader].shortOrderStopProfitPosition.isUsed) {
                    delete traderAllPositions[trader].shortOrderStopProfitPosition;
                    emit CloseStop(trader, token, side, StopType.TakeProfit, block.timestamp);
                }
                if (traderAllPositions[trader].shortOrderStopLossPosition.isUsed) {
                    delete traderAllPositions[trader].shortOrderStopLossPosition;
                    emit CloseStop(trader, token, side, StopType.StopLoss, block.timestamp);
                }
            } else {
                traderAllPositions[trader].short.size = traderAllPositions[trader].short.size - size;
                traderAllPositions[trader].short.timestamp = block.timestamp;
            }
        }
        // compute and operate trading fee
        uint256 tradingFee = size * closePrice * tradingFeeRatio / CONTRACT_DECIMAL / CONTRACT_DECIMAL;
        _operateTradingFee(tradingFee, trader, theBroker);
        // compute and operate position change fee
        int256 positionChangeFee;
        if (tradeType != TradeType.AllCloseHedgePart) {
            int256 pcfRatioAfter = DerifyMath.abs(_getPositionChangeFeeRatio());
            int256 nakedPositionAfter = DerifyMath.abs(int256(longTotalSize) - int256(shortTotalSize));
            positionChangeFee = _getPositionChangeFee(nakedPositionAfter - nakedPositionBefore, pcfRatioBefore + pcfRatioAfter);
            _operatePositionChangeFee(positionChangeFee, trader);
        }
        // emit event
        emit Close(
            trader,
            token,
            tradeType,
            side,
            size,
            closePrice,
            tradingFee,
            positionChangeFee,
            unrealizedPnl,
            block.timestamp
        );
    }

    /// @dev operate trading fee
    function _operateTradingFee(
        uint256 tradingFee,
        address trader,
        address theBroker
    ) internal {
        IDerifyExchange(exchange).operateTradingFee(
            tradingFee, 
            trader, 
            theBroker,
            tradingFeePmrRatio, 
            tradingFeeInusranceRatio,
            tradingFeeBrokerRatio
        );
    }

    /// @dev operate position change fee
    function _operatePositionChangeFee(
        int256 positionChangeFee,
        address trader
    ) internal {
        IDerifyExchange(exchange).operateTraderBalance(
            IDerifyExchange.FeeType.PositionChangeFee, 
            trader, 
            positionChangeFee * -1
        );
    }

    /// @dev operate profit and loss
    function _operateProfitAndLoss(
        int256 unrealizedPnl,
        address trader
    ) internal {
        IDerifyExchange(exchange).operateTraderBalance(IDerifyExchange.FeeType.ProfitAndLoss, trader, unrealizedPnl);
    }

    /// @dev get unrelaized profit and loss with fixed size position
    function _getUnrealizedPnlFixedSize(
        Side side, 
        address trader, 
        uint256 fixedSize,
        uint256 spotPrice
    ) internal view returns (int256 unrealizedPnl) {
        if (side == Side.LONG) {
            unrealizedPnl = int256(fixedSize) * (int256(spotPrice) - int256(traderAllPositions[trader].long.price)) / int256(CONTRACT_DECIMAL);
        }
        if (side == Side.SHORT) {
            unrealizedPnl = int256(fixedSize) * (int256(traderAllPositions[trader].short.price) - int256(spotPrice)) / int256(CONTRACT_DECIMAL);
        }
    }

    /// @dev get latest price
    function _getSpotPrice() internal view returns (uint256) {
        (
            uint80 roundID, 
            int price,
            uint startedAt,
            uint timeStamp,
            uint80 answeredInRound
        ) = priceFeed.latestRoundData();

        return uint256(price);
    }

    /// @dev get position change fee ratio
    function _getPositionChangeFeeRatio() internal view returns (int256 ratio) {
        uint256 liquidityPoolBalance = IDerifyExchange(exchange).liquidityPool();
        (uint256 longTotalAmount, uint256 shortTotalAmount) = _getSideTotalAmount();
        int256 diffAmount = int256(longTotalAmount) - int256(shortTotalAmount);
        ratio = DerifyHelper.computePositionChangeFeeRatio(
            diffAmount,
            liquidityPoolBalance,
            kRatio,
            gRatio
        );
    }

    /// @dev get position change fee
    function _getPositionChangeFee(
        int256 nakedPositionDiff,
        int256 ratioSum
    ) internal view returns (int256 positionChangeFee) {
        uint256 spotPrice = _getSpotPrice();
        int256 thirdPart = DerifyMath.abs(ratioSum) / 2 + int256(roRatio);
        positionChangeFee = nakedPositionDiff * int256(spotPrice) / int256(CONTRACT_DECIMAL) * thirdPart / int256(CONTRACT_DECIMAL);
    }

    /// @dev get total position amount of one side
    function _getSideTotalAmount() internal view returns (
        uint256 longTotalAmount, 
        uint256 shortTotalAmount
    ) {
        uint256 spotPrice = _getSpotPrice();
        longTotalAmount = longTotalSize * spotPrice / CONTRACT_DECIMAL;
        shortTotalAmount = shortTotalSize * spotPrice / CONTRACT_DECIMAL;
    }
