// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "pancakeswap-peripheral/contracts/interfaces/IPancakeRouter02.sol";

import "./interfaces/IDerifyDerivative.sol";
import "./interfaces/IDerifyFactory.sol";
import "./interfaces/IDerifyRewards.sol";
import "./interfaces/IDerifyBroker.sol";
import "./interfaces/IDerifyExchange.sol";
import "./libraries/DerifyMath.sol";
import "./libraries/DerifyExchangeHelper.sol";

/// @title DerifyExchange - The decentralized derivatives exchange
contract DerifyExchange is IDerifyExchange, Context, Ownable, AccessControlEnumerable, ReentrancyGuard, Pausable {
    /// @dev Add the library methods
    using EnumerableSet for EnumerableSet.AddressSet;

    struct TraderAccountInfo {
        // account balance
        int256 accountBalance;
        // wether the account info is used
        bool isUsed;
    }

    /// @dev Create a new role identifier for the buy back role
    bytes32 public constant BUYBACK_ROLE = keccak256("BUYBACK_ROLE");
    /// @dev Create a new role identifier for the reward role
    bytes32 public constant REWARD_ROLE = keccak256("REWARD_ROLE");
    /// @dev Create a new role identifier for the clearing role
    bytes32 public constant CLEARING_ROLE = keccak256("CLEARING_ROLE");
    /// @dev Create a new role identifier for the fee role
    bytes32 public constant FEE_ROLE = keccak256("FEE_ROLE");
    /// @dev Create a new role identifier for the pauser role
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    /// @dev The contract decimals
    uint256 public constant CONTRACT_DECIMAL = 10 ** 8;
    /// @dev The token contract decimals diff with contract decimals
    uint256 public constant TOKEN_DECIMAL_DIFF = 10 ** 10;

    /// @dev the factory address 
    address public factory;
    /// @dev the derify rewards contract address 
    address public rewards;
    /// @dev the derify broker contract address 
    address public broker;
    /// @dev the derify clearing contract address 
    address public clearing;
    /// @dev the token USD address 
    address public tokenUSD;
    /// @dev the token DRF contract address 
    address public tokenDRF;
    /// @dev the DEX Router contract address 
    address public dexRouter;

    /// @inheritdoc IDerifyExchange
    uint256 public override thetaRatio;
    /// @inheritdoc IDerifyExchange
    uint256 public override buyBackFundRatio;
    /// @inheritdoc IDerifyExchange
    uint256 public override minOpenAmount;
    /// @inheritdoc IDerifyExchange
    int256 public override totalAccountBalance;
    /// @inheritdoc IDerifyExchange
    uint256 public override liquidityPool;
    /// @inheritdoc IDerifyExchange
    uint256 public override insuranceFundPool;

    /// @dev the account info of trader
    mapping(address => TraderAccountInfo) private traderAccountInfo;
    /// @dev the set of all traders
    EnumerableSet.AddressSet private allTraders;

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
    }

    receive() external payable {
        emit Received(_msgSender(), msg.value);
    }

    /// @inheritdoc IDerifyExchange
    function initialize(
        address _factory,
        address _rewards,
        address _broker,
        address _clearing,
        address _buyBackExecutor,
        address _tokenUSD,
        address _tokenDRF,
        address _dexRouter,
        uint256 _thetaRatio,
        uint256 _buyBackFundRatio,
        uint256 _minOpenAmount
    ) external override onlyOwner {
        factory = _factory;
        rewards = _rewards;
        broker = _broker;
        clearing = _clearing;
        tokenUSD = _tokenUSD;
        tokenDRF = _tokenDRF;
        dexRouter = _dexRouter;
        thetaRatio = _thetaRatio;
        buyBackFundRatio = _buyBackFundRatio;
        minOpenAmount = _minOpenAmount;
        // setup roles
        _setupRole(BUYBACK_ROLE, _buyBackExecutor);
        _setupRole(REWARD_ROLE, _rewards);
        _setupRole(CLEARING_ROLE, _clearing);
        _setupRole(FEE_ROLE, _clearing);
    }

    /// @inheritdoc IDerifyExchange
    function deposit(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // check status
        require (amount > 0, "DE: D_AMOUNT_ERR");
        TraderAccountInfo storage accountInfo = traderAccountInfo[trader];
        if (!allTraders.contains(trader) && !accountInfo.isUsed) {
            allTraders.add(trader);
            accountInfo.isUsed = true;
        }
        // deposit
        accountInfo.accountBalance += int256(amount);
        totalAccountBalance += int256(amount);
        liquidityPool += amount;
        require(IERC20(tokenUSD).transferFrom(trader, address(this), amount * TOKEN_DECIMAL_DIFF), "DE: D_USD_TRANSFER_ERR");
        // emit events
        emit Deposit(trader, amount, accountInfo.accountBalance, block.timestamp);
        emit LiquidityPoolUpdate(PoolUpdateType.LiquidityDeposit, int256(amount), liquidityPool, block.timestamp);
    }
    
    /// @inheritdoc IDerifyExchange
    function withdraw(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // check status
        TraderAccountInfo storage accountInfo = traderAccountInfo[trader];
        require (accountInfo.isUsed, "DE: W_TRADER_ERR");
        address[] memory tokens = _getCreatedDerivativeTokens();
        require (amount <= _getAvailableMargin(tokens, trader), "DE: W_AMOUNT_EXCEED");
        // compute compensation ratio
        uint256 executeAmount = amount;
        (int256 sysNetPnl, int256 allTradersMarginBalance) = _getCompensationParams();
        int256 compensation = int256(amount) * sysNetPnl / allTradersMarginBalance;
        if (compensation > 0) {
            uint256 profitCompensation = uint256(compensation);
           if (insuranceFundPool >= profitCompensation) {     
                _transferPoolFrom(
                    PoolUpdateType.ProfitCompensation, 
                    AccountType.InsuranceFundPool, 
                    AccountType.LiquidityPool, 
                    profitCompensation
                );
            } else {
                // handle the case that InsuranceFundPool not enough to pay => pay bond
                uint256 pnlBond = profitCompensation - insuranceFundPool;
                executeAmount = amount - pnlBond;
                _transferPoolFrom(
                    PoolUpdateType.ProfitCompensation, 
                    AccountType.InsuranceFundPool, 
                    AccountType.LiquidityPool, 
                    insuranceFundPool
                );
                IDerifyRewards(rewards).payBond(trader, pnlBond);           
            }
        } else if (compensation < 0){
            uint256 lossCompensation = uint256(compensation * -1);
            _transferPoolFrom(
                PoolUpdateType.LossCompensation, 
                AccountType.LiquidityPool, 
                AccountType.InsuranceFundPool, 
                lossCompensation
            );
        }
        // withdraw
        accountInfo.accountBalance -= int256(amount);
        totalAccountBalance -= int256(amount);
        liquidityPool -= executeAmount;
        require(IERC20(tokenUSD).transfer(trader, executeAmount * TOKEN_DECIMAL_DIFF), "DE: W_USD_TRANSFER_ERR");
        // emit events
        emit Withdraw(trader, int256(amount) * -1, accountInfo.accountBalance, block.timestamp);
        emit LiquidityPoolUpdate(
            PoolUpdateType.LiquidityWithdraw, 
            int256(executeAmount) * -1, 
            liquidityPool, 
            block.timestamp
        );
    }

    /// @inheritdoc IDerifyExchange
    function openPosition(
        address theBroker,
        address token, 
        IDerifyDerivative.Side side, 
        OpenType openType,
        QuantityType quantityType,
        uint256 quantity, 
        uint256 price, 
        uint256 leverage
    ) external override whenNotPaused {
        address trader = _msgSender();
        // convert amount to size
        uint256 usedPrice = price;
        if (openType == OpenType.MarketOrder) {
            usedPrice = _getDerivative(token).getSpotPrice();
        }
        uint256 size = quantity;
        if (quantityType == QuantityType.Amount) {
            require(quantity >= minOpenAmount, "DE_OP: MIN_OPEN_AMOUNT");
            size = quantity * CONTRACT_DECIMAL / usedPrice;
        } else {
            require(quantity * usedPrice >= minOpenAmount, "DE_OP: MIN_OPEN_AMOUNT");
        }

        // check max open size
        uint256 maxOpenSize = _getOpenUpperBound(token, trader, side, openType, price, leverage);
        if (maxOpenSize > 0) {
            size = DerifyMath.min(maxOpenSize, size);
            //@dev: hedge position has no sys upper bound, only consider trader upper bound
            if (side == IDerifyDerivative.Side.HEDGE) {
                require (openType == OpenType.MarketOrder, "DE: OP_ERR_TYPE_HEDGE");
                _getDerivative(token).openMarketPricePosition(
                    IDerifyDerivative.TradeType.HedgeMarketPriceOpen, 
                    trader, 
                    theBroker, 
                    IDerifyDerivative.Side.LONG, 
                    size / 2, 
                    usedPrice, 
                    leverage
                );
                _getDerivative(token).openMarketPricePosition(
                    IDerifyDerivative.TradeType.HedgeMarketPriceOpen, 
                    trader, 
                    theBroker, 
                    IDerifyDerivative.Side.SHORT, 
                    size / 2, 
                    usedPrice, 
                    leverage
                );
            } else {
                if (openType == OpenType.MarketOrder) {
                    _getDerivative(token).openMarketPricePosition(
                        IDerifyDerivative.TradeType.MarketPriceOpen, 
                        trader, 
                        theBroker, 
                        side, 
                        size, 
                        usedPrice, 
                        leverage
                    );
                } else if (openType == OpenType.LimitOrder) {
                    //@dev: limit order position only consider trader upper bound, ignore sys upper bound
                    _getDerivative(token).orderLimitPricePosition(trader, side, size, usedPrice, leverage);
                }
            }
        }
    }

    /// @inheritdoc IDerifyExchange
    function closePosition(
        address theBroker,
        address token,
        IDerifyDerivative.Side side, 
        uint256 size
    ) external override {
        address trader = _msgSender();
        uint256 maxSize = _getCloseUpperBound(token, trader, side);
        if (maxSize > 0) {
            if (size <= maxSize) {
                _getDerivative(token).closePositionByTrader(
                    IDerifyDerivative.TradeType.SingleClose, 
                    trader, 
                    theBroker, 
                    side, 
                    size
                );
            } else {
                _getDerivative(token).closePositionByTrader(
                    IDerifyDerivative.TradeType.SingleClose, 
                    trader, 
                    theBroker, 
                    side, 
                    maxSize
                );
            }
        }
    }

    /// @inheritdoc IDerifyExchange
    function closeAllPositions(address theBroker) external override {
        address trader = _msgSender();
        address[] memory tokens = _getCreatedDerivativeTokens();
        for(uint256 i = 0; i < tokens.length; i++) {
            _getDerivative(tokens[i]).closeDerivativePositions(trader, theBroker);
        }
    }

    /// @inheritdoc IDerifyExchange
    function cancelAllOrderedPositions() external override {
        address trader = _msgSender();
        address[] memory tokens = _getCreatedDerivativeTokens();
        for(uint256 i = 0; i < tokens.length; i++) {
            _getDerivative(tokens[i]).cancelDerivativeOrderedPositions(trader);
        }
    }

    /// @inheritdoc IDerifyExchange
    function operateTraderBalance(
        FeeType feeType, 
        address trader, 
        int256 amount
    ) external override onlyRole(FEE_ROLE) {
        _operateTraderBalance(feeType, trader, amount);
    }

    /// @inheritdoc IDerifyExchange
    function operateTradingFee(
        uint256 tradingFee,
        address trader,
        address theBroker,
        uint256 tradingFeePmrRatio, 
        uint256 tradingFeeInusranceRatio,
        uint256 tradingFeeBrokerRatio
    ) external override onlyRole(FEE_ROLE) {
        _operateTraderBalance(FeeType.TradingFee, trader, int256(tradingFee) * -1);
        (, , , , uint256 validPeriodInBlocks, uint256 startBlockNumber) = IDerifyBroker(broker).getBrokerInfo(theBroker);
        if (validPeriodInBlocks >= block.number - startBlockNumber) {
            // 40% to insurance pool
            _transferPoolFrom(
                PoolUpdateType.InsuranceTradingFee, 
                AccountType.LiquidityPool, 
                AccountType.InsuranceFundPool, 
                tradingFee * tradingFeeInusranceRatio / CONTRACT_DECIMAL
            );
            // 30% to broker reward
            _operateLiquidityBalance(
                tradingFee * tradingFeeBrokerRatio / CONTRACT_DECIMAL, 
                broker, 
                PoolUpdateType.BrokerTradingFee
            );
            IDerifyBroker(broker).addBrokerReward(
                theBroker, 
                trader, 
                tradingFee * tradingFeeBrokerRatio / CONTRACT_DECIMAL
            );
            
        } else {
            // 30%+40% to insurance pool
            _transferPoolFrom(
                PoolUpdateType.InsuranceTradingFee, 
                AccountType.LiquidityPool, 
                AccountType.InsuranceFundPool, 
                tradingFee * (tradingFeeBrokerRatio + tradingFeeInusranceRatio) / CONTRACT_DECIMAL
            );
        }
        // 30% to position mining reward
        _operateLiquidityBalance(
            tradingFee * tradingFeePmrRatio / CONTRACT_DECIMAL, 
            rewards, 
            PoolUpdateType.PositionMiningTradingFee
        );
        IDerifyRewards(rewards).addPositionReward(trader, tradingFee * tradingFeePmrRatio / CONTRACT_DECIMAL);
    }

    /// @inheritdoc IDerifyExchange
    function exchangeBondByInsuranceFund(
        address trader, 
        uint256 amount
    ) external override onlyRole(REWARD_ROLE) {
        require(insuranceFundPool >= amount, "DE: EBBIF_BALANCE_EXCEED");
        insuranceFundPool -= amount;
        require(IERC20(tokenUSD).transfer(trader, amount * TOKEN_DECIMAL_DIFF), "DE: EBBIF_USD_TRANSFER_ERR");
        emit InfurancePoolUpdate(PoolUpdateType.BondExchange, int256(amount) * -1, insuranceFundPool, block.timestamp);
    }

    /// @inheritdoc IDerifyExchange
    function transferPoolFrom(
        PoolUpdateType poolUpdateType, 
        AccountType from,
        AccountType to,
        uint256 value
    ) external override onlyRole(CLEARING_ROLE) {
        _transferPoolFrom(poolUpdateType, from, to, value);
    }

    /// @inheritdoc IDerifyExchange
    function removeTrader(address trader) external override onlyRole(CLEARING_ROLE) {
        allTraders.remove(trader);
        delete traderAccountInfo[trader];
        // remove trader of DerifyDerivatives
        address[] memory tokens = _getCreatedDerivativeTokens();
        for(uint256 i = 0; i < tokens.length; i++) {
            _getDerivative(tokens[i]).removeTrader(trader);
        }
    }

    /// @inheritdoc IDerifyExchange
    function buyBackDrf(
        uint256 amountIn, 
        uint256 amountOutMin, 
        uint256 deadline, 
        uint256 gasFee
    ) external override onlyRole(BUYBACK_ROLE) {
        address buybackExecutor = _msgSender();
        uint256 availableAmount = _getSysExchangeBondSizeUpperBound();
        require(availableAmount > amountIn + gasFee, "DE: BUY_BACK_AMOUNT_EXCEED");
        // buy DRF and burn it to address(1)
        address[] memory path = new address[](2);
        path[0] = tokenUSD;
        path[1] = tokenDRF;
        insuranceFundPool -= amountIn;
        IERC20(tokenUSD).approve(dexRouter, amountIn * TOKEN_DECIMAL_DIFF);
        uint[] memory amounts = IPancakeRouter02(dexRouter).swapExactTokensForTokens(
            amountIn * TOKEN_DECIMAL_DIFF, 
            amountOutMin * TOKEN_DECIMAL_DIFF, 
            path, 
            address(1), 
            deadline
        );
        emit InfurancePoolUpdate(PoolUpdateType.DrfBuyBack, int256(amountIn) * -1, insuranceFundPool, block.timestamp);
        // pay gas fee
        insuranceFundPool -= gasFee;
        require(IERC20(tokenUSD).transfer(buybackExecutor, gasFee * TOKEN_DECIMAL_DIFF), "DE: BBD_USD_TRANSFER_ERR");
        emit BuyBackDrfUpdate(amountIn, amounts[amounts.length - 1] / TOKEN_DECIMAL_DIFF, gasFee, block.timestamp);
        emit InfurancePoolUpdate(
            PoolUpdateType.GasFeeForBuyBack, 
            int256(gasFee) * -1, 
            insuranceFundPool, 
            block.timestamp
        );
    }

    /// @inheritdoc IDerifyExchange
    function pause() external override onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @inheritdoc IDerifyExchange
    function unpause() external override onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /// @inheritdoc IDerifyExchange
    function getSysExchangeBondSizeUpperBound() external view override returns (uint256 sysMaxBondSize) {
        sysMaxBondSize = _getSysExchangeBondSizeUpperBound();
    }

    /// @inheritdoc IDerifyExchange
    function getAllTraders() external view override returns (address[] memory) {
        return allTraders.values();
    }

    /// @inheritdoc IDerifyExchange
    function getTraderAccount(address trader) external view override returns (
        int256 balance, 
        int256 marginBalance, 
        uint256 totalMargin, 
        uint256 availableMargin
    ) {
        TraderAccountInfo memory accountInfo = traderAccountInfo[trader];
        require (accountInfo.isUsed, "DE: GTA_TRADER_ERR");
        address[] memory tokens = _getCreatedDerivativeTokens();
        balance = accountInfo.accountBalance;
        (marginBalance, , , ) = _getMarginBalance(tokens, trader);
        (uint256 totalHeldMargin, ) = _getTotalPositionMargin(tokens, trader);
        totalMargin = totalHeldMargin;
        availableMargin = _getAvailableMargin(tokens, trader);
    }

    /// @inheritdoc IDerifyExchange
    function getTraderVariables(address trader) external view override returns (
        int256 marginBalance, 
        uint256 totalPositionAmount, 
        int256 marginRate
    ) {
        address[] memory tokens = _getCreatedDerivativeTokens();
        // trader share variables
        (marginBalance, , , ) = _getMarginBalance(tokens, trader);
        totalPositionAmount = _getTotalPositionAmount(tokens, trader);
        if (totalPositionAmount > 0) {
            marginRate = marginBalance * int256(CONTRACT_DECIMAL) / int256(totalPositionAmount);
        }
    }

    /// @inheritdoc IDerifyExchange
    function getSysOpenUpperBound(
        address token, 
        IDerifyDerivative.Side side
    ) external view override returns (uint256 size) {
        size = _getSysOpenUpperBound(token, side);
    }

    /// @inheritdoc IDerifyExchange
    function getTraderOpenUpperBound(
        address token, 
        address trader,
        OpenType openType, 
        uint256 price, 
        uint256 leverage
    ) external view override returns (uint256 size, uint256 amount) {
        (size, amount) = _getTraderOpenUpperBound(token, trader, openType, price, leverage);
    }

    /// @inheritdoc IDerifyExchange
    function getCloseUpperBound(
        address token, 
        address trader,
        IDerifyDerivative.Side side
    ) external view override returns (uint256 size) {
        size = _getCloseUpperBound(token, trader, side);
    }

    /// @inheritdoc IDerifyExchange
    function getSysCloseUpperBound(
        address token, 
        IDerifyDerivative.Side side
    ) external view override returns (uint256 size) {
        size = _getSysCloseUpperBound(token, side);
    }

    /// @inheritdoc IDerifyExchange
    function getTraderPositionVariables(
        IDerifyDerivative.Side side,
        uint256 spotPrice,
        uint256 size,
        uint256 leverage,
        uint256 averagePrice
    ) external pure override returns (uint256 margin, int256 unrealizedPnl, int256 returnRate) {
        margin = DerifyExchangeHelper.computeMargin(size, leverage, spotPrice);
        unrealizedPnl = DerifyExchangeHelper.computeUnrealizedPnl(side, size, averagePrice, spotPrice);
        returnRate = DerifyExchangeHelper.computeReturnRate(unrealizedPnl, margin);
    }

    /// @notice Get the total position amount of the whole system
    /// @dev used by backend
    function getSysTotalPositionAmount() public view returns (uint256 sysTotalPositionAmount) {     
        address[] memory tokens = _getCreatedDerivativeTokens();
        for(uint256 i = 0; i < tokens.length; i++) {
            uint256 spotPrice = _getDerivative(tokens[i]).getSpotPrice();
            uint256 longTotalSize = _getDerivative(tokens[i]).longTotalSize();
            uint256 shortTotalSize = _getDerivative(tokens[i]).shortTotalSize();
            sysTotalPositionAmount += (longTotalSize + shortTotalSize) * spotPrice / CONTRACT_DECIMAL;
        }
    }

    /// @dev transfer between pools
    function _transferPoolFrom(
        PoolUpdateType poolUpdateType, 
        AccountType from,
        AccountType to,
        uint256 value
    ) internal {
        // operate from
        if (from == AccountType.LiquidityPool) {
            liquidityPool -= value;
            emit LiquidityPoolUpdate(poolUpdateType, int256(value) * -1, liquidityPool, block.timestamp);
        } else if (from == AccountType.InsuranceFundPool) {
            insuranceFundPool -= value;
            emit InfurancePoolUpdate(poolUpdateType, int256(value) * -1, insuranceFundPool, block.timestamp);
        }
        // operate to
        if (to == AccountType.LiquidityPool) {
            liquidityPool += value;
            emit LiquidityPoolUpdate(poolUpdateType, int256(value), liquidityPool, block.timestamp);
        } else if (to == AccountType.InsuranceFundPool) {
            insuranceFundPool += value;
            emit InfurancePoolUpdate(poolUpdateType, int256(value), insuranceFundPool, block.timestamp);
        }
    }

    /// @dev operate trader balance
    function _operateTraderBalance(
        FeeType feeType, 
        address trader, 
        int256 amount
    ) internal {
        traderAccountInfo[trader].accountBalance += amount;
        totalAccountBalance += amount;
        emit TraderBalanceUpdate(feeType, trader, amount, traderAccountInfo[trader].accountBalance, block.timestamp);

        if (feeType == FeeType.GasFee) {
            liquidityPool -= uint256(amount * -1);
            require(IERC20(tokenUSD).transfer(clearing, uint256(amount * -1) * TOKEN_DECIMAL_DIFF), "DE: OTB_USD_TRANSFER_ERR");
            emit LiquidityPoolUpdate(PoolUpdateType.GasFeeForClearing, amount, liquidityPool, block.timestamp);
        }
    }

    /// @dev used to avoid stack too deep
    function _operateLiquidityBalance(
        uint256 amount, 
        address to, 
        PoolUpdateType poolUpdateType
    ) internal {
        liquidityPool -= amount;
        require(IERC20(tokenUSD).transferFrom(address(this), to, amount * TOKEN_DECIMAL_DIFF), "DE: OLB_USD_TRANSFER_ERR");
        emit LiquidityPoolUpdate(poolUpdateType, int256(amount) * -1, liquidityPool, block.timestamp);
    }

    /// @dev Get upper bound of exchange bond size
    function _getSysExchangeBondSizeUpperBound() internal view returns (uint256 sysMaxBondSize) {
        (int256 sysNetPnl, ) = _getCompensationParams();
        sysNetPnl = DerifyMath.max(sysNetPnl, 0);

        int256 baseAmount = int256(insuranceFundPool) - sysNetPnl;
        baseAmount = DerifyMath.max(baseAmount, 0);
        sysMaxBondSize = buyBackFundRatio * uint256(baseAmount) / CONTRACT_DECIMAL;
    }

    /// @dev For the compensation mechanism
    function _getCompensationParams() internal view returns (
        int256 sysNetPnl, 
        int256 allTradersMarginBalance
    ) {
        address[] memory tokens = _getCreatedDerivativeTokens();
        int256 allTradersUnrealizedPnl;
        for(uint256 i = 0; i < tokens.length; i++) {
            allTradersUnrealizedPnl += _getDerivative(tokens[i]).getTokenTotalUnrealizedPnl();
        }
        allTradersMarginBalance = totalAccountBalance + allTradersUnrealizedPnl;

        sysNetPnl = allTradersMarginBalance - int256(liquidityPool);
    }

    /// @dev get open upper bound
    function _getOpenUpperBound(
        address token, 
        address trader, 
        IDerifyDerivative.Side side, 
        OpenType openType, 
        uint256 price, 
        uint256 leverage
    ) internal view returns (uint256 size){
        (size, ) = _getTraderOpenUpperBound(token, trader, openType, price, leverage);
        if (side == IDerifyDerivative.Side.HEDGE || openType == OpenType.LimitOrder) {
            return size;
        }

        uint256 sysMaxSize = _getSysOpenUpperBound(token, side);
        size = DerifyMath.min(sysMaxSize, size);
    }

    /// @dev get open upper bound of system
    function _getSysOpenUpperBound(
        address token, 
        IDerifyDerivative.Side side
    ) internal view returns (uint256 size) {
        uint256 spotPrice = _getDerivative(token).getSpotPrice();
        (uint256 longTotalAmount, uint256 shortTotalAmount) = _getDerivative(token).getSideTotalAmount();

        int256 diffAmount = int256(longTotalAmount) - int256(shortTotalAmount);
        int256 amount;
        if (side == IDerifyDerivative.Side.LONG) {
            amount = int256(liquidityPool * thetaRatio / CONTRACT_DECIMAL) - diffAmount;
        } else if (side == IDerifyDerivative.Side.SHORT) {
            amount = int256(liquidityPool * thetaRatio / CONTRACT_DECIMAL) + diffAmount;
        }

        if (amount > 0) {
            size = uint256(amount) * CONTRACT_DECIMAL / spotPrice;
        }
    }

    /// @dev get open upper bound of trader
    function _getTraderOpenUpperBound(
        address token, 
        address trader,
        OpenType openType, 
        uint256 price, 
        uint256 leverage
    ) internal view returns (uint256 size, uint256 amount) {
        address[] memory tokens = _getCreatedDerivativeTokens();
        uint256 availableMargin = _getAvailableMargin(tokens, trader);
        uint256 usedPrice = price;
        ///@dev: use the lastest spot price instead of the price given by front-end
        if (openType == OpenType.MarketOrder) {
            usedPrice = _getDerivative(token).getSpotPrice();
        }
        require(usedPrice != 0, "DE: GTOPB_DIV_ZERO");
        amount = uint256(availableMargin) * leverage / CONTRACT_DECIMAL;
        size = amount * CONTRACT_DECIMAL / usedPrice;
    }

    /// @dev get close upper bound
    function _getCloseUpperBound(
        address token, 
        address trader,
        IDerifyDerivative.Side side
    ) internal view returns (uint256 size) {
        IDerifyDerivative.DerivativePositions memory allPositions = _getDerivative(token).getTraderDerivativePositions(trader);
        if (side == IDerifyDerivative.Side.LONG) {
            size = allPositions.long.size;
        } else {
            size = allPositions.short.size;
        }

        uint256 sysMaxSize = _getSysCloseUpperBound(token, side);
        size = DerifyMath.min(sysMaxSize, size);
    }

    /// @dev get close upper bound of system
    function _getSysCloseUpperBound(
        address token, 
        IDerifyDerivative.Side side
    ) internal view returns (uint256 size) {
        uint256 spotPrice = _getDerivative(token).getSpotPrice();
        (uint256 longTotalAmount, uint256 shortTotalAmount) = _getDerivative(token).getSideTotalAmount();

        int256 diffAmount = int256(longTotalAmount) - int256(shortTotalAmount);
        int256 amount;
        if (side == IDerifyDerivative.Side.LONG) {
            amount = int256(liquidityPool * thetaRatio / CONTRACT_DECIMAL) + diffAmount;
        } else if (side == IDerifyDerivative.Side.SHORT) {
            amount = int256(liquidityPool * thetaRatio / CONTRACT_DECIMAL) - diffAmount;
        }
        if (amount > 0) {
            size = uint256(amount) * CONTRACT_DECIMAL / spotPrice;
        }
    }

    /// @dev get available margin of trader
    function _getAvailableMargin(
        address[] memory tokens, 
        address trader
    ) internal view returns (uint256 availableMargin) {
        (int256 marginBalance, , ,) = _getMarginBalance(tokens, trader);
        (uint256 totalHeldMargin, uint256 totalOrderedMargin) = _getTotalPositionMargin(tokens, trader);
        int256 tmpValue = marginBalance - int256(totalHeldMargin + totalOrderedMargin);
        if (tmpValue > 0) {
            availableMargin = uint256(tmpValue);
        } else {
            availableMargin = 0;
        }
    }

    /// @dev get margin balance of trader
    function _getMarginBalance(address[] memory tokens, address trader) internal view returns (
        int256 marginBalance, 
        int256 traderUnrealizedProfit, 
        int256 traderUnrealizedLoss, 
        bool hasPosition
    ) {
        TraderAccountInfo memory accountInfo = traderAccountInfo[trader];
        require (accountInfo.isUsed, "DE: GMB_TRADER_ERR");
        
        for(uint256 i = 0; i < tokens.length; i++) {
            uint256 spotPrice = _getDerivative(tokens[i]).getSpotPrice();
            int256 pnl;
            IDerifyDerivative.DerivativePositions memory allPositions = _getDerivative(tokens[i]).getTraderDerivativePositions(trader);
            if (allPositions.long.size > 0) {
                pnl = DerifyExchangeHelper.computeUnrealizedPnl(
                    IDerifyDerivative.Side.LONG, 
                    allPositions.long.size, 
                    allPositions.long.price, 
                    spotPrice
                );
                if (pnl >= 0) {
                    traderUnrealizedProfit += pnl;
                } else {
                    traderUnrealizedLoss += pnl;
                }
                hasPosition = true;
            }

            if (allPositions.short.size > 0) {
                pnl = DerifyExchangeHelper.computeUnrealizedPnl(
                    IDerifyDerivative.Side.SHORT, 
                    allPositions.short.size, 
                    allPositions.short.price, 
                    spotPrice
                );
                if (pnl >= 0) {
                    traderUnrealizedProfit += pnl;
                } else {
                    traderUnrealizedLoss += pnl;
                }
                hasPosition = true;
            }
        }

        marginBalance = accountInfo.accountBalance + traderUnrealizedProfit + traderUnrealizedLoss;
    }

    /// @dev get total position amount of trader
    function _getTotalPositionAmount(
        address[] memory tokens, 
        address trader
    ) internal view returns (uint256 totalPositionAmount) {     
        for(uint256 i = 0; i < tokens.length; i++) {
            uint256 spotPrice = _getDerivative(tokens[i]).getSpotPrice();
            IDerifyDerivative.DerivativePositions memory allPositions = _getDerivative(tokens[i]).getTraderDerivativePositions(trader);
            totalPositionAmount += allPositions.long.size * spotPrice / CONTRACT_DECIMAL;
            totalPositionAmount += allPositions.short.size * spotPrice / CONTRACT_DECIMAL; 
        }
    }

    /// @dev get total position margin of trader
    function _getTotalPositionMargin(
        address[] memory tokens, 
        address trader
    ) internal view returns (uint256 totalHeldMargin, uint256 totalOrderedMargin) {
        for(uint256 i = 0; i < tokens.length; i++) {
            uint256 spotPrice = _getDerivative(tokens[i]).getSpotPrice();

            IDerifyDerivative.DerivativePositions memory allPositions = _getDerivative(tokens[i]).getTraderDerivativePositions(trader);
            if (allPositions.long.isUsed) {
                totalHeldMargin += DerifyExchangeHelper.computeMargin(
                    allPositions.long.size, 
                    allPositions.long.leverage, 
                    spotPrice
                );
            }
            if (allPositions.short.isUsed) {
                totalHeldMargin += DerifyExchangeHelper.computeMargin(
                    allPositions.short.size, 
                    allPositions.short.leverage, 
                    spotPrice
                );
            }
            for(uint256 j = 0; j < allPositions.longOrderOpenPosition.length; j++) {
                if (allPositions.longOrderOpenPosition[j].isUsed) {
                    totalOrderedMargin += DerifyExchangeHelper.computeMargin(
                        allPositions.longOrderOpenPosition[j].size, 
                        allPositions.longOrderOpenPosition[j].leverage, 
                        allPositions.longOrderOpenPosition[j].price
                    );
                }
            }
            for(uint256 k = 0; k < allPositions.shortOrderOpenPosition.length; k++) {
                if (allPositions.shortOrderOpenPosition[k].isUsed) {
                    totalOrderedMargin += DerifyExchangeHelper.computeMargin(
                        allPositions.shortOrderOpenPosition[k].size, 
                        allPositions.shortOrderOpenPosition[k].leverage, 
                        allPositions.shortOrderOpenPosition[k].price
                    );
                }
            }
        }
    }

    /// @dev Returns all the tokens that created derivative
    function _getCreatedDerivativeTokens() internal view returns (address[] memory) {
        return IDerifyFactory(factory).getCreatedDerivativeTokens();
    }

    /// @dev Returns the derivative for the given token. The derivative contract may or may not exist.
    function _getDerivative(address token) internal view returns (IDerifyDerivative) {
        return IDerifyDerivative(IDerifyFactory(factory).getDerivative(token));
    }
}
