// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@derify/derify-token/contracts/CommunityVestingVault.sol";

import "./interfaces/IDerifyRewards.sol";
import "./interfaces/IDerifyExchange.sol";
import "./interfaces/IDerifyERC20.sol";

/// @title DerifyRewards - Process rewards of bond, edrf, position mining rewards, broker rewards.
contract DerifyRewards is IDerifyRewards, Context, Ownable, AccessControl, ReentrancyGuard, Pausable {
    /// @dev struct to store bond info of trader
    struct TraderBondInfo {
        uint256 bondBalance;
        uint256 bondReturnBalance;
        uint256 blockNumber;
        bool    isUsed;
    }
    /// @dev struct to store staking info of trader
    struct TraderStakingInfo {
        uint256 drfBalance;
        uint256 edrfBalance;
        uint256 blockNumber;
        bool    isUsed;
    }
    /// @dev struct to store position mining reward info of trader
    struct TraderRewardInfo {
        uint256 usdRewardBalance;
        uint256 accumulatedUsdReward;
        uint256 drfRewardBalance;
        uint256 accumulatedDrfReward;
    }

    /// @dev Create a new role identifier for the pauser role
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    /// @dev Create a new role identifier for the exchange role
    bytes32 public constant EXCHANGE_ROLE = keccak256("EXCHANGE_ROLE");
    /// @dev Create a new role identifier for the reward execute role
    bytes32 public constant REWARD_EXECUTE_ROLE = keccak256("REWARD_EXECUTE_ROLE");

    /// @dev The contract decimals
    uint256 public constant CONTRACT_DECIMAL = 10 ** 8;
    /// @dev The token contract decimals diff with contract decimals
    uint256 public constant TOKEN_DECIMAL_DIFF = 10 ** 10;
    /// @dev The seconds of year
    uint256 public constant YEAR_SECONDS = 31536000;

    /// @dev the token USDT contract address 
    address public tokenUSD;
    /// @dev the token bBUSD contract address 
    address public tokenBBUSD;
    /// @dev the token DRF contract address 
    address public tokenDRF;
    /// @dev the token eDRF contract address 
    address public tokenEDRF;
    /// @dev the DerifyExchange contract address 
    address public exchange;
    /// @dev the CommunityVestingVault contract address 
    address public communityVestingVault;

    /// @inheritdoc IDerifyRewards
    uint256 public override blockTime;
    /// @inheritdoc IDerifyRewards
    uint256 public override bondAnnualInterestRate;
    /// @inheritdoc IDerifyRewards
    uint256 public override unitStakingNumber;
    /// @inheritdoc IDerifyRewards
    uint256 public override bankBondPool;
    /// @inheritdoc IDerifyRewards
    uint256 public override stakingDrfPool;
    /// @inheritdoc IDerifyRewards
    uint256 public override usdPmrPool;
    /// @inheritdoc IDerifyRewards
    uint256 public override drfPmrPool;

    /// @dev all traders with bond
    address[] private allBondTraders;
    /// @dev all traders with stking
    address[] private allStakingTraders;
    /// @dev trader bond info
    mapping(address => TraderBondInfo) private traderBondInfo;
    /// @dev trader staking info
    mapping(address => TraderStakingInfo) private traderStakingInfo;
    /// @dev trader position mining reward info
    mapping(address => TraderRewardInfo) private traderRewardInfo;
    
    constructor(
        address _exchange,
        address _tokenUSD, 
        address _tokenBBUSD,
        address _tokenDRF,
        address _tokenEDRF,
        address _communityVestingVault,
        uint256 _blockTime,
        uint256 _bondAnnualInterestRate,
        uint256 _unitStakingNumber
    ) {
        exchange = _exchange;
        tokenUSD = _tokenUSD;
        tokenBBUSD = _tokenBBUSD;
        tokenDRF = _tokenDRF;
        tokenEDRF = _tokenEDRF;
        communityVestingVault = _communityVestingVault;
        blockTime = _blockTime;
        bondAnnualInterestRate = _bondAnnualInterestRate;
        unitStakingNumber = _unitStakingNumber;
        //setup roles
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        _setupRole(REWARD_EXECUTE_ROLE, _msgSender());
        _setupRole(EXCHANGE_ROLE, _exchange);
    }

    /// @inheritdoc IDerifyRewards
    function updateParams(
        address _exchange,
        address _tokenUSD, 
        address _tokenBBUSD,
        address _tokenDRF,
        address _tokenEDRF,
        address _communityVestingVault,
        uint256 _blockTime,
        uint256 _bondAnnualInterestRate,
        uint256 _unitStakingNumber
    ) external override onlyOwner {
        exchange = _exchange;
        tokenUSD = _tokenUSD;
        tokenBBUSD = _tokenBBUSD;
        tokenDRF = _tokenDRF;
        tokenEDRF = _tokenEDRF;
        communityVestingVault = _communityVestingVault;
        blockTime = _blockTime;
        bondAnnualInterestRate = _bondAnnualInterestRate;
        unitStakingNumber = _unitStakingNumber;

        emit ContractParamsUpdate(
            exchange, 
            tokenUSD,
            tokenBBUSD,
            tokenDRF,
            tokenEDRF,
            communityVestingVault,
            blockTime,
            bondAnnualInterestRate,
            unitStakingNumber
        );
    }

    /// @inheritdoc IDerifyRewards
    function pause() external override onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @inheritdoc IDerifyRewards
    function unpause() external override onlyRole(PAUSER_ROLE) {
        _unpause();
    }
    
    /// @inheritdoc IDerifyRewards
    function payBond(address trader, uint256 amount) external override onlyRole(EXCHANGE_ROLE) {
        if (!traderBondInfo[trader].isUsed) {
            allBondTraders.push(trader);
            traderBondInfo[trader].isUsed = true;
        }
        traderBondInfo[trader].bondBalance += amount;
        IDerifyERC20(tokenBBUSD).mint(address(this), amount * TOKEN_DECIMAL_DIFF);
        emit BondUpdates(
            UpdateType.Income, 
            trader, 
            int256(amount), 
            traderBondInfo[trader].bondBalance, 
            block.timestamp, 
            block.number
        );
    }

    /// @inheritdoc IDerifyRewards
    function withdrawAllBond() external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // update bBUSD balance, block number
        _addBondByAction(trader);
        // withdraw all bBUSD
        uint256 amount = traderBondInfo[trader].bondBalance;
        traderBondInfo[trader].bondBalance = 0;
        require(IDerifyERC20(tokenBBUSD).transfer(trader, amount * TOKEN_DECIMAL_DIFF), "DBond: WAB_BOND_TRANSFER_ERR");
        emit BondUpdates(
            UpdateType.Withdraw, 
            trader, 
            int256(amount) * -1, 
            traderBondInfo[trader].bondBalance, 
            block.timestamp, 
            block.number
        );
    }

    /// @inheritdoc IDerifyRewards
    function exchangeBond(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // check system bond limit
        uint256 maxBondSize = getExchangeBondSizeUpperBound(trader);
        require(maxBondSize >= amount, "DBond: EB_MAX_SIZE_EXCEED");
        // update bBUSD balance, block number
        _addBondByAction(trader);
        // exchange bBUSD
        IDerifyERC20(tokenBBUSD).burn(trader, amount * TOKEN_DECIMAL_DIFF);
        IDerifyExchange(exchange).exchangeBondByInsuranceFund(trader, amount);
    }

    /// @inheritdoc IDerifyRewards
    function depositBondToBank(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // update bBUSD balance, block number
        _addBondByAction(trader);
        // deposit bBUSD
        uint256 traderWalletBalance = (IDerifyERC20(tokenBBUSD).balanceOf(trader)) / TOKEN_DECIMAL_DIFF;
        require(traderWalletBalance >= amount, "DBond: DBTB_BALANCE_EXCEED");
        require(IDerifyERC20(tokenBBUSD).transferFrom(trader, address(this), amount * TOKEN_DECIMAL_DIFF), "DBond: DBTB_BOND_TRANSFER_ERR");
        if (!traderBondInfo[trader].isUsed) {
            allBondTraders.push(trader);
            traderBondInfo[trader].isUsed = true;
        }
        traderBondInfo[trader].bondReturnBalance += amount;
        bankBondPool += amount;
        emit BankBondPoolUpdate(UpdateType.Income, trader, int256(amount), bankBondPool, block.timestamp);
    }

    /// @inheritdoc IDerifyRewards
    function redeemBondFromBank(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // update bBUSD balance, block number
        _addBondByAction(trader);
        // redeem bBUSD
        require(traderBondInfo[trader].bondReturnBalance >= amount, "DBond: RBTB_BALANCE_EXCEED");
        traderBondInfo[trader].bondReturnBalance -= amount;
        bankBondPool -= amount;
        require(IDerifyERC20(tokenBBUSD).transfer(trader, amount * TOKEN_DECIMAL_DIFF), "DBond: RBTB_BOND_TRANSFER_ERR");
        emit BankBondPoolUpdate(UpdateType.Withdraw, trader, int256(amount) * -1, bankBondPool, block.timestamp);
    }

    /// @inheritdoc IDerifyRewards
    function stakingDrf(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // check trader exists
        if (!traderStakingInfo[trader].isUsed) {
            allStakingTraders.push(trader);
            traderStakingInfo[trader].isUsed = true;
        }
        // update edrf balance, block number
        _addEdrfByAction(trader);
        // staking and update drf balance
        traderStakingInfo[trader].drfBalance += amount;
        stakingDrfPool += amount;
        require(IERC20(tokenDRF).transferFrom(trader, address(this), amount * TOKEN_DECIMAL_DIFF), "DStaking: SD_DRF_TRANSFER_ERR");
        // emit event
        emit DrfBalanceUpdate(
            UpdateType.Income, 
            trader, 
            int256(amount), 
            traderStakingInfo[trader].drfBalance, 
            block.timestamp
        );
        emit StakingDrfPoolUpdate(UpdateType.Income, trader, int256(amount), stakingDrfPool, block.timestamp);
    }

    /// @inheritdoc IDerifyRewards
    function redeemDrf(uint256 amount) external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        require(traderStakingInfo[trader].drfBalance >= amount, "DStaking: RD_BALANCE_EXCEED");
        // update edrf balance, block number
        _addEdrfByAction(trader);
        // redeem and update drf balance
        traderStakingInfo[trader].drfBalance -= amount;
        stakingDrfPool -= amount;
        require(IERC20(tokenDRF).transfer(trader, amount * TOKEN_DECIMAL_DIFF), "DStaking: RD_DRF_TRANSFER_ERR");
        // emit event
        emit DrfBalanceUpdate(
            UpdateType.Withdraw, 
            trader, 
            int256(amount) * -1, 
            traderStakingInfo[trader].drfBalance, 
            block.timestamp
        );
        emit StakingDrfPoolUpdate(UpdateType.Withdraw, trader, int256(amount) * -1, stakingDrfPool, block.timestamp);
    }

    /// @inheritdoc IDerifyRewards
    function withdrawAllEdrf() external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // update edrf balance, block number
        _addEdrfByAction(trader);
        // withdraw and update edrf balance
        uint256 amount = traderStakingInfo[trader].edrfBalance;
        traderStakingInfo[trader].edrfBalance = 0;
        require(IDerifyERC20(tokenEDRF).transfer(trader, amount * TOKEN_DECIMAL_DIFF), "DStaking: WAE_EDRF_TRANSFER_ERR");
        // emit event
        emit EdrfBalanceUpdate(
            UpdateType.Withdraw, 
            trader, 
            int256(amount) * -1, 
            traderStakingInfo[trader].edrfBalance, 
            block.timestamp, 
            block.number
        );
    }

    /// @inheritdoc IDerifyRewards
    function addPositionReward(address trader, uint256 amount) external override onlyRole(EXCHANGE_ROLE) {
        usdPmrPool += amount;
        emit UsdPmrPoolUpdate(UpdateType.Income, trader, int256(amount), usdPmrPool, block.timestamp);
    }

    /// @inheritdoc IDerifyRewards
    function claimDrfPmr() external override onlyRole(REWARD_EXECUTE_ROLE) {
        (, uint256 amountVested) = CommunityVestingVault(communityVestingVault).calculateGrantClaim(address(this));
        CommunityVestingVault(communityVestingVault).claimVestedTokens();
        drfPmrPool += amountVested / TOKEN_DECIMAL_DIFF;
        emit DrfPmrPoolUpdate(
            UpdateType.Income, 
            communityVestingVault, 
            int256(amountVested / TOKEN_DECIMAL_DIFF), 
            drfPmrPool, 
            block.timestamp
        );
    }

    /// @inheritdoc IDerifyRewards
    function updateTradersPositionReward(
        address[] calldata traders, 
        uint256[] calldata usdAmounts,
        uint256[] calldata drfAmounts, 
        uint256 gasFeeAmount
    ) external override onlyRole(REWARD_EXECUTE_ROLE) {
        uint256 len = traders.length;
        require(len == usdAmounts.length, "DPMR: UTPMR_USD_LEN_ERR");
        require(len == drfAmounts.length, "DPMR: UTPMR_DRF_LEN_ERR");
        for(uint256 i = 0; i < len; ) {
            address trader = traders[i];
            uint256 usdAmount = usdAmounts[i];
            uint256 drfAmount = drfAmounts[i];
            traderRewardInfo[trader].usdRewardBalance += usdAmount;
            traderRewardInfo[trader].accumulatedUsdReward += usdAmount;

            traderRewardInfo[trader].drfRewardBalance += drfAmount;
            traderRewardInfo[trader].accumulatedDrfReward += drfAmount;
            emit PositionRewardUpdates(
                UpdateType.Income, 
                trader, 
                int256(usdAmount), 
                traderRewardInfo[trader].usdRewardBalance, 
                int256(drfAmount), 
                traderRewardInfo[trader].drfRewardBalance, 
                block.timestamp
            );
            unchecked {
                ++i;
            }
        }
        require(IERC20(tokenUSD).transfer(_msgSender(), gasFeeAmount * TOKEN_DECIMAL_DIFF), "DPMR: UTPMR_USD_TRANSFER_ERR");
    }

    /// @inheritdoc IDerifyRewards
    function withdrawPositionReward() external override nonReentrant whenNotPaused {
        address trader = _msgSender();
        // withdraw usd reward
        uint256 usdAmount = traderRewardInfo[trader].usdRewardBalance;
        if (usdAmount > 0) {
            require(usdPmrPool >= usdAmount, "DPMR: WPMR_USD_BALANCE_EXCEED");
            traderRewardInfo[trader].usdRewardBalance -= usdAmount;
            usdPmrPool -= usdAmount;
            require(IERC20(tokenUSD).transfer(trader, usdAmount * TOKEN_DECIMAL_DIFF), "DPMR: WPMR_USD_TRANSFER_ERR");
            emit UsdPmrPoolUpdate(UpdateType.Withdraw, trader, int256(usdAmount) * -1, usdPmrPool, block.timestamp);
        }
        // withdraw drf reward
        uint256 drfAmount = traderRewardInfo[trader].drfRewardBalance;
        if (drfAmount > 0) {
            require(drfPmrPool >= drfAmount, "DPMR: WPMR_DRF_BALANCE_EXCEED");
            traderRewardInfo[trader].drfRewardBalance -= drfAmount;
            drfPmrPool -= drfAmount;
            require(IERC20(tokenDRF).transfer(trader, drfAmount * TOKEN_DECIMAL_DIFF), "DPMR: WPMR_DRF_TRANSFER_ERR");
            emit DrfPmrPoolUpdate(UpdateType.Withdraw, trader, int256(drfAmount) * -1, drfPmrPool, block.timestamp);
        }
        if (usdAmount > 0 || drfAmount > 0) {
            emit PositionRewardUpdates(
                UpdateType.Withdraw, 
                trader, 
                int256(usdAmount) * -1, 
                traderRewardInfo[trader].usdRewardBalance, 
                int256(drfAmount) * -1, 
                traderRewardInfo[trader].drfRewardBalance, 
                block.timestamp
            );
        }
    }

    /// @inheritdoc IDerifyRewards
    function getPositionReward(address trader) external view override returns (
        uint256 usdBalance, 
        uint256 usdAccumulatedBalance,
        uint256 drfBalance, 
        uint256 drfAccumulatedBalance
    ) {
        usdBalance = traderRewardInfo[trader].usdRewardBalance;
        usdAccumulatedBalance = traderRewardInfo[trader].accumulatedUsdReward;
        drfBalance = traderRewardInfo[trader].drfRewardBalance;
        drfAccumulatedBalance = traderRewardInfo[trader].accumulatedDrfReward;
    }

    /// @inheritdoc IDerifyRewards
    function getBondInfo(address trader) external view override returns (
        uint256 bondBalance, 
        uint256 bondReturnBalance, 
        uint256 lastUpdatedBlockNumber,
        uint256 bondWalletBalance, 
        uint256 bondAnnualInterestRatio
    ) {
        bondBalance = traderBondInfo[trader].bondBalance;
        bondReturnBalance = traderBondInfo[trader].bondReturnBalance;
        lastUpdatedBlockNumber = traderBondInfo[trader].blockNumber;
        bondWalletBalance = (IDerifyERC20(tokenBBUSD).balanceOf(trader)) / TOKEN_DECIMAL_DIFF;
        bondAnnualInterestRatio = bondAnnualInterestRate;
    }

    /// @inheritdoc IDerifyRewards
    function getAllBondTraders() external view override returns (address[] memory) {
        return allBondTraders;
    }

    /// @inheritdoc IDerifyRewards
    function getStakingInfo(address trader) external view override returns (
        uint256 drfBalance, 
        uint256 edrfBalance,
        uint256 lastUpdatedBlockNumber
    ) {
        drfBalance = traderStakingInfo[trader].drfBalance;
        edrfBalance = traderStakingInfo[trader].edrfBalance;
        lastUpdatedBlockNumber = traderStakingInfo[trader].blockNumber;
    }

    /// @inheritdoc IDerifyRewards
    function getAllStakingTraders() external view override returns (address[] memory) {
        return allStakingTraders;
    }

    /// @dev get the upper bound of available exchange bond size
    function getExchangeBondSizeUpperBound(
        address trader
    ) public view returns (uint256 maxBondSize) {
        maxBondSize = (IDerifyERC20(tokenBBUSD).balanceOf(trader)) / TOKEN_DECIMAL_DIFF;

        uint256 sysMaxBondSize = IDerifyExchange(exchange).getSysExchangeBondSizeUpperBound();
        if (sysMaxBondSize < maxBondSize) {
            maxBondSize = sysMaxBondSize;
        }
    }

    /// @dev update bond when trader does actions
    function _addBondByAction(address trader) internal {
        uint256 addedBond = traderBondInfo[trader].bondReturnBalance * (bondAnnualInterestRate / YEAR_SECONDS * blockTime) * (block.number - traderBondInfo[trader].blockNumber) / CONTRACT_DECIMAL / CONTRACT_DECIMAL;
        traderBondInfo[trader].bondBalance += addedBond;
        traderBondInfo[trader].blockNumber = block.number;
        IDerifyERC20(tokenBBUSD).mint(address(this), addedBond * TOKEN_DECIMAL_DIFF);
        emit BondUpdates(
            UpdateType.Interest, 
            trader, 
            int256(addedBond), 
            traderBondInfo[trader].bondBalance, 
            block.timestamp, 
            block.number
        );
    }

    /// @dev update edrf when trader does actions
    function _addEdrfByAction(address trader) internal {
        // update edrf balance, block number
        uint256 addedEdrf = traderStakingInfo[trader].drfBalance * (unitStakingNumber * (block.number - traderStakingInfo[trader].blockNumber)) / CONTRACT_DECIMAL;
        traderStakingInfo[trader].edrfBalance += addedEdrf;
        traderStakingInfo[trader].blockNumber = block.number;
        IDerifyERC20(tokenEDRF).mint(address(this), addedEdrf * TOKEN_DECIMAL_DIFF);
        emit EdrfBalanceUpdate(
            UpdateType.Income, 
            trader, 
            int256(addedEdrf) * -1, 
            traderStakingInfo[trader].edrfBalance, 
            block.timestamp, 
            block.number
        );
    }
}
