// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "../interfaces/IDerifyERC20.sol";

/// @title bBUSD - bond of BUSD token contract.
contract bBUSD is IDerifyERC20, Context, IERC20Metadata, Ownable, Pausable {
    /// @inheritdoc IDerifyERC20
    address public override _minter;

    string private constant _name = "bond of BUSD";
    string private constant _symbol = "bBUSD";
    string private constant _version = "1.0";
    uint8  private constant _decimals = 18;

    uint256 private _totalSupply;
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;

    /// @notice Set the value for {minter}.
    constructor(address minter) {
        _minter = minter;
        emit MinterChanged(address(0), _minter);
    }

    /// @notice Returns the name of the token.
    function name() external view virtual override returns (string memory) {
        return _name;
    }

    /// @notice Returns the symbol of the token.
    function symbol() external view virtual override returns (string memory) {
        return _symbol;
    }

    /// @notice Returns the number of decimals used to get its user representation.
    function decimals() external view virtual override returns (uint8) {
        return _decimals;
    }

    /// @notice Returns the version of the token.
    function version() external view virtual returns (string memory) {
        return _version;
    }

    /// @inheritdoc IERC20
    function totalSupply() external view virtual override returns (uint256) {
        return _totalSupply;
    }

    /// @inheritdoc IERC20
    function balanceOf(address account) external view virtual override returns (uint256) {
        return _balances[account];
    }

    /// @inheritdoc IERC20
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /// @inheritdoc IDerifyERC20
    function pause() external virtual override onlyOwner {
        _pause();
    }

    /// @inheritdoc IDerifyERC20
    function unpause() external virtual override onlyOwner {
        _unpause();
    }

    /// @inheritdoc IDerifyERC20
    function setMinter(address minter) external virtual override onlyOwner {
        require(minter != address(0), "bBUSD::setMinter: minter as the zero address");
        emit MinterChanged(_minter, minter);
        _minter = minter;
    }

    /// @inheritdoc IDerifyERC20
    function mint(address dst, uint256 amount) external virtual override {
        address minter = _msgSender();
        require(minter == _minter, "bBUSD::mint: only the minter can mint");
        _mint(dst, amount);
    }

    /// @inheritdoc IDerifyERC20
    function burn(address from, uint256 amount) external virtual override {
        address spender = _msgSender();
        if (from != spender) {
            _spendAllowance(from, spender, amount);
        }
        _burn(from, amount);
    }

    /// @inheritdoc IERC20
    function approve(address spender, uint256 amount) external virtual override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    /// @notice Increases the allowance granted to `spender` by the caller.
    function increaseAllowance(address spender, uint256 addedValue) external virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    /// @notice Decreases the allowance granted to `spender` by the caller.
    function decreaseAllowance(address spender, uint256 subtractedValue) external virtual returns (bool) {
        address owner = _msgSender();
        uint256 currentAllowance = allowance(owner, spender);
        require(currentAllowance >= subtractedValue, "bBUSD::decreaseAllowance: decreased allowance below zero");
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    /// @inheritdoc IERC20
    function transfer(address to, uint256 amount) external virtual override returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    /// @inheritdoc IERC20
    function transferFrom(
        address from, 
        address to, 
        uint256 amount
    ) external virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "bBUSD::_approve: approve from the zero address");
        require(spender != address(0), "bBUSD::_approve: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "bBUSD::_transfer: transfer from the zero address");
        require(to != address(0), "bBUSD::_transfer: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "bBUSD::_transfer: transfer amount exceeds balance");
        unchecked {
            _balances[from] = fromBalance - amount;
        }
        _balances[to] += amount;

        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "bBUSD::_spendAllowance: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "bBUSD: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "bBUSD: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "bBUSD: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual whenNotPaused {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
